var fieldInfos = [];
var attributes = {};

// 11/11/2022, 3:18 PM
if (!IsEmpty($feature.WashingTime)){
  Push(fieldInfos, {fieldName: "Time washing done"});
	attributes["Time washing done"] = Text($feature.WashingTime, "DD/MM/Y, h:mm A");
}

if (!IsEmpty($feature.WashingPerson)){
  Push(fieldInfos, {fieldName: "Washing performed by"});
	attributes["Washing performed by"] = $feature.WashingPerson;
}
if (Count(fieldInfos) > 0){
  return {
      type: 'fields',
      title: 'Washing',
      fieldInfos:  fieldInfos,
      attributes : attributes
    }
  }
  else {
    return null;
  }

